import React, { useState, useEffect } from 'react';
import GameSettings from '../components/game/GameSettings';
import GameBoard from '../components/game/GameBoard';
import GameAI, { findBestMove, makeMove, checkWinner } from '../components/gameAI';

export default function Game() {
  const [gameStarted, setGameStarted] = useState(false);
  const [board, setBoard] = useState(null);
  const [currentPlayer, setCurrentPlayer] = useState(1);
  const [gameOver, setGameOver] = useState(false);
  const [winner, setWinner] = useState(null);
  const [winningCells, setWinningCells] = useState(null);
  const [settings, setSettings] = useState(null);
  const [isThinking, setIsThinking] = useState(false);
  const [hintsLeft, setHintsLeft] = useState(0);
  const [hintColumn, setHintColumn] = useState(null);

  const initializeBoard = (rows, cols) => {
    return Array(rows).fill().map(() => Array(cols).fill(0));
  };

  const handleStartGame = (gameSettings) => {
    setSettings(gameSettings);
    setBoard(initializeBoard(gameSettings.rows, gameSettings.cols));
    setGameStarted(true);
    setCurrentPlayer(1);
    setGameOver(false);
    setWinner(null);
    setWinningCells(null);
    setHintsLeft(gameSettings.hints);
    setHintColumn(null);
  };

  const checkForTie = (board) => {
    const isFullBoard = board[0].every(cell => cell !== 0);
    if (isFullBoard) {
      return true;
    }
    return false;
  };

  const handleHint = () => {
    if (hintsLeft > 0 && currentPlayer === 1 && !gameOver) {
      const { column } = findBestMove(board, settings.level, false, -Infinity, Infinity);
      setHintColumn(column);
      setHintsLeft(prev => prev - 1);
    }
  };

  const handleCellClick = (col) => {
    if (gameOver || currentPlayer !== 1) return;

    setHintColumn(null);
    const newBoard = makeMove(board, col, 1);
    setBoard(newBoard);

    const result = checkWinner(newBoard);
    if (result) {
      setGameOver(true);
      setWinner(result.winner);
      setWinningCells(result.winningCells);
      return;
    }

    if (checkForTie(newBoard)) {
      setGameOver(true);
      setWinner(0);
      return;
    }

    setCurrentPlayer(2);
  };

  useEffect(() => {
    if (currentPlayer === 2 && !gameOver) {
      setIsThinking(true);
      setTimeout(() => {
        const { column } = findBestMove(board, settings.level, true, -Infinity, Infinity);
        const newBoard = makeMove(board, column, 2);
        setBoard(newBoard);

        const result = checkWinner(newBoard);
        if (result) {
          setGameOver(true);
          setWinner(result.winner);
          setWinningCells(result.winningCells);
        } else if (checkForTie(newBoard)) {
          setGameOver(true);
          setWinner(0);
        } else {
          setCurrentPlayer(1);
        }
        setIsThinking(false);
      }, 500);
    }
  }, [currentPlayer, board, settings?.level]);

  if (!gameStarted) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50 py-12">
        <GameSettings onStartGame={handleStartGame} />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-purple-50 py-8">
      <GameBoard
        board={board}
        onCellClick={handleCellClick}
        gameOver={gameOver}
        winner={winner}
        winningCells={winningCells}
        onRestart={() => handleStartGame(settings)}
        onNewGame={() => setGameStarted(false)}
        currentPlayer={currentPlayer}
        isThinking={isThinking}
        hintsLeft={hintsLeft}
        onHint={handleHint}
        hintColumn={hintColumn}
      />
    </div>
  );
}
